// ==UserScript==
// @name         حساب معدل طلاب الهندسة المعلوماتية في الجامعة الافتراضية السورية
// @namespace    http://svu-ise.com/
// @encoding     utf-8
// @version      2018.07.19
// @description  لحساب معدل طلاب الهندسة المعلوماتية في الجامعة الافتراضية السورية
// @author       زهير طه
// @include      /(.+)svuonline\.org(.+)/
// @require      https://ajax.googleapis.com/ajax/libs/jquery/3.2.1/jquery.min.js
// @icon 	     http://svu-ise.com/files/img/avg.ico
// @homepage 	 http://svu-ise.com/avg
// @updateURL    https://raw.githubusercontent.com/zuhairtaha/avgCalc/master/avg_update.js
// @downloadURL  https://github.com/zuhairtaha/avgCalc/raw/master/svu_ise_average.user.js
// @grant        none
// ==/UserScript==

(function () {

  //region style
  let style = $('<style>.tahasoft{background:blue none repeat scroll 0 0;border-radius: 5px;box-shadow: 1px 1px 3px #e7e7e7;margin: 3px;color:white;float:left;padding:8px;text-shadow:none;width:258px;overflow: hidden;}.tahasoft p{float:left;font-family:"segoe ui",arial,tahoma,serif;font-size:16px;line-height:24px;}.tahasoft img{float:right;padding-right:10px;}.ts1{background:#fc6a00 none repeat scroll 0 0;}.ts2{background:#028ccb none repeat scroll 0 0;}.ts3{background:#fba400 none repeat scroll 0 0;}.ts4{background:#00bb71 none repeat scroll 0 0;}.avgDiv{background:#fff;margin:auto;width:100%;padding:24px 0;text-align:center;font-size:17px;font-family:arial serif;text-shadow:1px 1px #FCF5CF;direction:rtl;display:inline-block}#lastUpdateAnchor{font-family:tahoma,serif;target=_blank;float:right;margin-right:13px;font-size:9px;margin-top:3px;color:#808080}.chart{display:table;table-layout:fixed;width:60%;max-width:700px;height:200px;margin:0 auto;background-image:linear-gradient(to top,rgba(0,0,0,.1) 2%,rgba(0,0,0,0) 2%);background-size:100% 50px;background-position:left top}.chart li{position:relative;display:table-cell;vertical-align:bottom;height:200px}.chart span{margin:0 1em;display:block;background:rgba(209,236,250,.75);animation:draw 1s ease-in-out}.chart span:before{position:absolute;left:0;right:0;top:100%;padding:5px 1em 0;display:block;text-align:center;content:attr(title);word-wrap:break-word}@keyframes draw{0%{height:0}}</style>');

  $('html > head').append(style);
  //endregion

  //region images variables
  let img1 = 'data:image/png;base64,';
  let img2 = 'data:image/png;base64,';
  let img3 = 'data:image/png;base64,';
  let img4 = 'data:image/png;base64,';
  //endregion

  //region variables
  let last_update = "19-7-2018";
  let TotalHoursWithoutSkippedEnglish = 0;
  let countOfPassedSubjects = 0;
  let numerator = 0;
  let denominator = 0;
  let EnglishHoursPassedArray = [];
  let EnglishHoursPassed = 0;
  let year = '';
  let NxtYerar = '';
  let sumEquivalent = 0;
  let remainToNxt = 0;
  let remain = 0;
  let totlaHours = 0;
  let LastDivInRow;
  let markDiv;
  let SubjectCode;
  let mark;
  let CreditHours;
  let assignmentDiv;
  let classDiv;
  let examDiv;
  let assignmentMark;
  let examMark;
  let semester;
  let s;
  let html;
  let help;
  let evaluation;
  let subjectsArray = [];
  let average;
  let assignmentDiv1;
  let assignmentDiv2;
  let assignmentDiv3;
  let assignmentDiv4;
  let assignmentDiv5;
  let assignmentDiv6;
  let helpDiv;
  let bb = 0;
  let mm = 0;
  let semestersArray = [];
  let semestersAveragesDiv;
//endregion

  // region loop table divs
  $('tr td.tdpadding_c a').each(function () {
    let SpecialStatusAnchorTitle = $(this).attr('title');
    if (SpecialStatusAnchorTitle === 'Passed ()' || SpecialStatusAnchorTitle === 'Registered ()') {
      LastDivInRow = $(this).parent().siblings(':last');

      classDiv = $(this).parent().next().next().next();
      assignmentDiv1 = classDiv.next().next();
      assignmentDiv2 = assignmentDiv1.next();
      assignmentDiv3 = assignmentDiv2.next();
      assignmentDiv4 = assignmentDiv3.next();
      assignmentDiv5 = assignmentDiv4.next();
      assignmentDiv6 = assignmentDiv5.next();
      examDiv = assignmentDiv6.next();
      markDiv = examDiv.next();
      helpDiv = markDiv.next();

      SubjectCode = LastDivInRow.parent().next().children('.tdpadding_c:first-child').next().next().next().html();
      mark = parseInt(markDiv.html());
      examMark = parseFloat(examDiv.html());
      assignmentMark = 0;

      help = helpDiv.html() === "Helped";
      if (!isNaN(parseFloat(assignmentDiv1.html()))) assignmentMark += parseFloat(assignmentDiv1.html());
      if (!isNaN(parseFloat(assignmentDiv2.html()))) assignmentMark += parseFloat(assignmentDiv2.html());
      if (!isNaN(parseFloat(assignmentDiv3.html()))) assignmentMark += parseFloat(assignmentDiv3.html());
      if (!isNaN(parseFloat(assignmentDiv4.html()))) assignmentMark += parseFloat(assignmentDiv4.html());
      if (!isNaN(parseFloat(assignmentDiv5.html()))) assignmentMark += parseFloat(assignmentDiv5.html());
      if (!isNaN(parseFloat(assignmentDiv6.html()))) assignmentMark += parseFloat(assignmentDiv6.html());
      semester = classDiv.html();
      s = semester;
      semester = 20 + semester.substr(semester.length - 2);
      if (SubjectCode === 'BAT') return;
      if (SubjectCode === 'PT') {
        markDiv.css("background", "#BCFFF8");
        if (mark >= 0 && mark < 35) EnglishHoursPassedArray.push(0);
        if (mark >= 35 && mark < 55) EnglishHoursPassedArray.push(3);
        if (mark >= 55 && mark < 73) EnglishHoursPassedArray.push(6);
        if (mark >= 73 && mark < 84) EnglishHoursPassedArray.push(9);
        if (mark >= 84 && mark < 95) EnglishHoursPassedArray.push(12);
        if (mark >= 95 && mark < 100) EnglishHoursPassedArray.push(15);
      }
      if (mark >= 50 && mark <= 100 && SubjectCode !== 'PT' || help === true) {
        // console.log(s + " : semester = " + semester + ", assignmentMark = " + Math.round(assignmentMark) + ", examMark = " + Math.round(examMark));
        if (
          (parseInt(semester) >= 2014) &&
          help === false &&
          (Math.round(assignmentMark) < 12 || Math.round(examMark) < 28) &&
          (!isNaN(assignmentMark) && !isNaN(examMark))
        ) {
          // console.log('continue...');
          return true;
        }
        //
        countOfPassedSubjects++;
        markDiv.css({"background": "#54BF48", "color": "#fff"});
        assignmentDiv1.css({"background": "#fff6f2"});
        assignmentDiv2.css({"background": "#fff6f2"});
        assignmentDiv3.css({"background": "#fff6f2"});
        assignmentDiv4.css({"background": "#fff6f2"});
        assignmentDiv5.css({"background": "#fff6f2"});
        assignmentDiv6.css({"background": "#fff6f2"});
        CreditHours = LastDivInRow.parent().next().children('.tdpadding_c:first-child').next().next().next().next().next().css('background', '#FFF87B').html();
        CreditHours = parseInt(CreditHours) < 3 ? 3 : parseInt(CreditHours);
        numerator = numerator + mark * CreditHours;
        denominator = denominator + CreditHours * 1;
        TotalHoursWithoutSkippedEnglish = 165 - denominator;
        // console.log('hours=' + denominator + ", subject=" + SubjectCode)
        subjectsArray.push({
          "Semester"   : s.substr(s.length - 3),
          "Subject"    : SubjectCode,
          "Mark"       : mark,
          "CreditHours": CreditHours
        });

      }
    }
    if (SpecialStatusAnchorTitle === 'Equivalent ()') {
      LastDivInRow = $(this).parent().siblings(':last');
      let courseCode = LastDivInRow.parent().next().children('.tdpadding_c:first-child').next().next().next().html();
      if (courseCode === "L1" || courseCode === "L2" || courseCode === "L3" || courseCode === "L4" || courseCode === "L5") return;
      CreditHours = LastDivInRow.parent().next().children('.tdpadding_c:first-child').next().next().next().next().next().css('background', 'orange').html();
      CreditHours = parseInt(CreditHours);
      sumEquivalent += CreditHours;
    }
  });
  //endregion

  // region calculate: average, Total hours, remain hours
  let myJSON = JSON.stringify(subjectsArray);
  // console.log(myJSON);

  $.each(subjectsArray, function (i, obj) {
    if (semestersArray.indexOf(obj.Semester) === -1 && (obj.Semester).length > 1)
      semestersArray.push(obj.Semester);
    bb += obj.Mark * obj.CreditHours;
    mm += obj.CreditHours;
  });

  average = (bb / mm).toFixed(2);
  if (EnglishHoursPassedArray.length > 0)
    EnglishHoursPassed = EnglishHoursPassedArray[0];
  if (average >= 50 && average < 60) evaluation = 'مقبول';
  if (average >= 60 && average < 70) evaluation = 'جيد';
  if (average >= 70 && average < 80) evaluation = 'جيد جداً';
  if (average >= 80 && average < 90) evaluation = 'ممتاز';
  if (average >= 90 && average < 100) evaluation = 'مرتبة شرف';
  totlaHours = denominator + EnglishHoursPassed + sumEquivalent;
  // remain     = TotalHoursWithoutSkippedEnglish - EnglishHoursPassed;
  remain = 159 - totlaHours;
  // endregion

  // region all semesters averages

  let semestersAverages = [];
  let b, m;
  for (let i = 0; i < semestersArray.length; i++) {
    b = 0;
    m = 0;
    $.each(subjectsArray, function (j, obj) {
      if (obj.Semester === semestersArray[i]) {
        b += obj.Mark * obj.CreditHours;
        m += obj.CreditHours;
      }
    });
    semestersAverages.push({
      "semester": semestersArray[i],
      "average" : (b / m).toFixed(2)
    });
  }
  console.log(semestersAverages);
  semestersAveragesDiv = '<ul class="chart">';
  for (let i = 0; i < semestersAverages.length; i++) {
    let sAvg = Math.round(semestersAverages[i].average);
    if (sAvg === 0) continue;
    semestersAveragesDiv += '<li><span style="height:' + sAvg + '%" ' +
      'title="' + semestersAverages[i].semester + ' ' + sAvg + '"></span></li>';
  }
  semestersAveragesDiv += '</ul><br/>';
  //endregion

  // region calculate :current year, next year, remain credit hours to next year
  if (totlaHours > 0 && totlaHours <= 30) {
    year = 'الثالثة';
    remainToNxt = 30 - totlaHours;
    NxtYerar = 'رابعة';
  }
  if (totlaHours > 30 && totlaHours <= 159) {
    year = 'الرابعة';
    remainToNxt = 159 - totlaHours;
    NxtYerar = 'تخرج';
  }
  if (totlaHours >= 159) {
    year = 'مبارك التخرج';
    remainToNxt = 0;
    NxtYerar = '';
  }
  // endregion

  //region set html results
  html = '<div class="avgDiv"><div class="tahasoft ts1"><img src="' + img1 + '">';
  html += '<p>المعدل<br /> ' + average + ' (' + evaluation + ') </p>';
  html += '</div><div class="tahasoft ts2"><img src="' + img2 + '">';
  if (year === 'الخامسة') {
    html += '<p>السنة الحالية: ' + year + '<br /><span style="font-size:13px">باقي ' + remainToNxt + ' ساعات للتخرج ' + '<span></p>';
  } else {
    if (totlaHours >= 147)
      html += '<p>السنة الحالية: ' + year + '<br /><span style="font-size:13px"> * * * * * <span></p>';
    else
      html += '<p>السنة الحالية: ' + year + '<br /><span style="font-size:13px">باقي ' + remainToNxt + ' لتصبح سنة ' + NxtYerar + '<span></p>';
  }
  html += '</div><div class="tahasoft ts3"><img src="' + img3 + '">';
  html += '<p>الساعات المتبقية للتخرج <br />' + remain + ' </p>';
  html += '</div><div class="tahasoft ts4"><img src="' + img4 + '">';
  html += '<p>عدد الساعات المجتازة <br /> ' + totlaHours + ' </p>';
  html += '</div></div>';
  $('.act_link').before(semestersAveragesDiv);
  $('.act_link').before(html);
  $(".ts4").after("<a id='lastUpdateAnchor' target='_blank' href='http://svu-ise.com/avg'>last update: " + last_update + "</a>");
  // endregion

})();
